var StatusReport = new Buffer ([0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x73, 0x74]);
var Wait = new Buffer ([0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x77, 0x74]);

//Intervall für Nachrichten senden
var delay = 800;

// ein 2-Byte-Zähler für alle Nachrichten
var highCounter = 0;
var lowCounter = 0;

// ein Indikator welche Nachricht diesesmal zu senden ist
var indicator = 0;

//Setze Timer der gemäß Intervall ausführt
setInterval(()=>{
    switch(indicator){
        case 0:
            Wait[9] = highCounter;
            Wait[10] = lowCounter;
            node.send({payload:Wait});
            node.done();
            indicator++; //Indikator hochzählen
            break;
        case 1:
            StatusReport[9] = highCounter;
            StatusReport[10] = lowCounter;
            node.send({payload:StatusReport});
            node.done();
            indicator++; //Indikator hochzählen
            break;
        case 2:
            LCDStatus[9] = highCounter;
            LCDStatus[10] = lowCounter;            
            node.send({payload:LCDStatus});
            node.done();
            indicator++; //Indikator hochzählen
            break;
        case 3:
            StatusReport[9] = highCounter;
            StatusReport[10] = lowCounter;
            node.send({payload:StatusReport});
            node.done();
            indicator++; //Indikator zurücksetzen
            break;
    }

    //Nachrichtenzähler hochzählen nachdem Nachricht gesendet wurde
    lowCounter++;

    //Sollte lowCounter > 255 sein (lowCounter > 0xFF), wird der highCounter erhöht und der lowCounter zurück auf 0 gesetzt
    if (lowCounter > 255) {
        highCounter++;
        lowCounter = 0;
    }
    //Sollte highCounter > 255 sein (highCounter > 0xFF), wird auch der highCounter zurück auf 0 gesetzt
    if (highCounter > 255) {
        highCounter = 0;
    }
}, delay);

